#include <iostream>
#include <cmath>
#include <vector>
#include <climits>

using namespace std;

int main()
{
	string tallet = "";
	
	unsigned int i = 0; // inkrement i loop
	unsigned int iVerdi = 0; // verdien av i som skal lagres ved h√∏yeste produkt
	
	unsigned long long maksProdukt = 0; // initialiserer
	const int REKKE = 12; // hvor mange tall fremover i tillegg til i som skal regnes ut
	
	while((i + REKKE) != tallet.length())
	{
		unsigned long long tempProdukt = 0;
		cout << "Kalkulerer tall: ";
		for(int j = i; j <= (i + REKKE); j++)
		{
			int siffer = tallet[j] - '0';
			
			if(0 == tempProdukt)
				tempProdukt = siffer;
			else
				tempProdukt *= siffer;
			
			cout << siffer;
		}
		cout << "\nTempProdukt: " << tempProdukt << endl;
		
		if(tempProdukt > maksProdukt)
			maksProdukt = tempProdukt;
		
		tempProdukt = 0;
		i++;
	}
	
	cout << "\nMaks produkt: " << maksProdukt << endl;
}
